INSERT INTO `maps` VALUES ('1', '长安城广场', '无', '[\"长安城历来人声鼎沸，车水马龙。小贩的吆喝声，小孩的嬉笑声此起彼伏。还有一些江湖艺人在表演着他们的拿手绝活，吸引了好多游手好闲的观众在围观。小贩不断在你身边叫卖着，你不禁被这繁华的景象吸引住了。\"]', '1', '2', '3', '4', '5', '1');
INSERT INTO `maps` VALUES ('2', '玄武大街', '无', '[\"这里的路相当的宽，能容好几匹马车并行，长长的道路通向北方。\"]', '1', '10', '15', '21', '1', '1');
INSERT INTO `maps` VALUES ('3', '白虎大街', '无', '[\"你走在一条宽阔的石板大街上，东面就快到城中心了。\"]', '1', '26', '27', '1', '28', '1');
INSERT INTO `maps` VALUES ('4', '青龙大街', '无', '[\"这里的路相当的宽，能容好几匹马车并行，长长的道路通向东方。\"]', '1', '29', '1', '30', '31', '1');
INSERT INTO `maps` VALUES ('5', '朱雀大街', '无', '[\"这是一条宽阔的青石板街道，向南北两头延伸,你走在一条繁忙的街道上，看着操着南腔北调的人们行色匆匆。\"]', '1', '1', '713', '6', '43', '1');
INSERT INTO `maps` VALUES ('6', '长安客栈（江湖起点）', '无', '[\"长安客栈是江湖人物出没的地方，拜师可以找柳如烟、清法比丘、无名乞丐。在江湖百晓生那里可以领任务。\"]', '1', '44', '5', '7', '880', '0');
INSERT INTO `maps` VALUES ('7', '江湖聚义厅', '无', '[\"江湖豪杰相会于此。在这里你可以申请成为新人的师傅，也可以申请一个师傅带你玩这个游戏。\"]', '3', '53', '6', '8', '45', '0');
INSERT INTO `maps` VALUES ('8', '新手村广场', '无', '[\"欢迎你来到风云变幻的江湖！祝你早日成为一代大侠。先从木人开始练手吧，也可以和武林信使对话做任务。\"]', '3', '46', '7', '50', '52', '0');
INSERT INTO `maps` VALUES ('9', '长安赌坊', '无', '[\"赌场里围着黑压压的一群人，正在掷骰子押大小。这里赌的要比外面的大许多，赌场对这儿的赌客也特别照顾，在这儿沏茶倒水的小妞都比外面的漂亮多了。\"]', '1', '24', '0', '0', '21', '0');
INSERT INTO `maps` VALUES ('10', '练武场', '无', '[\"这里是长安守军的操练的地方，一对官兵正在操练，两旁放置这一些剑，刀之类的武器，中间的空地上放置着好多的木人。\"]', '1', '11', '14', '20', '2', '1');
INSERT INTO `maps` VALUES ('11', '玄武门', '无', '[\"城墙以条石砌基，巨砖砌身，城砖用优质粘土和白瓷土烧成，非常坚固。虽经年月的风吹雨打，至今还完好无损地保存下来。\"]', '1', '12', '0', '0', '10', '1');
INSERT INTO `maps` VALUES ('12', '驿道', '长安北驿道', '[\"一条青石铺就的驿道由南向北延伸，顺着驿道往北就离开长安城了。\"]', '1', '13', '0', '23', '11', '1');
INSERT INTO `maps` VALUES ('13', '渭水南岸', '无', '[\"这里是渭水南岸，河面上来来往往的船只很多。\"]', '1', '0', '0', '0', '12', '1');
INSERT INTO `maps` VALUES ('14', '长安药铺', '无', '[\"这是一家药铺，一股浓浓的药味让你几欲窒息。\"]', '1', '0', '0', '10', '0', '0');
INSERT INTO `maps` VALUES ('15', '茅草屋', '无', '[\"一间破破烂烂的茅草屋，好像有很长的岁月。\"]', '1', '0', '19', '2', '16', '1');
INSERT INTO `maps` VALUES ('16', '江湖名人堂', '无', '[\"当你的武功和声望达到一定程度后就可以进入名人堂。\"]', '1', '15', '0', '0', '0', '1');
INSERT INTO `maps` VALUES ('17', '夺宝神殿', '无', '[\"这里就是传说中的夺宝神殿。\"]', '1', '0', '0', '0', '18', '0');
INSERT INTO `maps` VALUES ('18', '集气门', '无', '[\"这里是传说中的集气门。\"]', '1', '17', '0', '0', '19', '0');
INSERT INTO `maps` VALUES ('19', '时空隧道', '无', '[\"在这里，你可以选择回到从前。\"]', '1', '18', '0', '15', '0', '1');
INSERT INTO `maps` VALUES ('20', '长安铁匠铺', '无', '[\"你刚走进来，就感到这里热气腾腾，四周的墙上挂着铁器，大部分都是已经打好的铁锤，长剑，菜刀，匕首等物。中间摆着个旺盛的火炉，一个铁匠正在火炉旁卖力的敲打着。\"]', '1', '0', '10', '0', '0', '0');
INSERT INTO `maps` VALUES ('21', '烟雨楼', '无', '[\"方圆数百里内，烟雨楼的名气可谓是不小。来此落脚的名士雅客也为其题匾作诗，流下不少令其得意的东西。楼下布置简易，顾客多是匆匆的行人。\"]', '1', '9', '2', '0', '0', '1');
INSERT INTO `maps` VALUES ('22', '树林深处', '无', '[\"这里是树林深处，一株株巨大的树木突现在眼前,微风过去,树叶发出簌簌的响声,远处传来几声狼嗷。\"]', '1', '0', '0', '0', '23', '1');
INSERT INTO `maps` VALUES ('23', '树林', '无', '[\"这一片阴森的树林。,林子里很少有灌木从,全是高耸入云的千年古树.树木的枝梢交错着,伸展开来的繁盛的枝叶如碧绿的云,把蓝天遮了个严严实实。\"]', '1', '22', '12', '0', '0', '1');
INSERT INTO `maps` VALUES ('24', '奇珍斋', '无', '[\"听说这里的物品是外面都买不到的，这里的老板非常的神秘哦。\"]', '1', '0', '0', '0', '9', '0');
INSERT INTO `maps` VALUES ('26', '相记钱庄', '无', '[\"这是一家老字号的钱庄，相老板是山西人，这家钱庄从他的爷爷的爷爷的爷爷的爷爷那辈开始办起。\"]', '1', '0', '0', '0', '3', '1');
INSERT INTO `maps` VALUES ('27', '白虎门', '无', '[\"城墙以条石砌基，巨砖砌身，城砖用优质粘土和白瓷土烧成，非常坚固。虽经年月的风吹雨打，至今还完好无损地保存下来。\"]', '1', '0', '611', '3', '0', '1');
INSERT INTO `maps` VALUES ('28', '阿绣当铺', '无', '[\"这是长安城里最大的一家当铺了素来以买卖公平着称。\"]', '1', '3', '0', '0', '0', '0');
INSERT INTO `maps` VALUES ('29', '化生寺', '无', '[\"这是一座洒扫得很干净的寺庙。偶尔有几声鸟叫，院子里静悄悄的。\"]', '1', '716', '0', '0', '4', '1');
INSERT INTO `maps` VALUES ('30', '青龙门', '无', '[\"这是长安城的东城门，。高耸坚固的城墙透出一股威严。\"]', '1', '0', '4', '665', '0', '1');
