INSERT INTO `maps` VALUES ('37', '静修斋', '无', '[\"一间静悄悄的屋子，一位鹤发童颜的老人在屋子中间翻阅着古书，似乎在研究些什么。\"]', '1', '36', '0', '0', '38', '0');
