INSERT INTO `maps` VALUES ('43', '朱雀南街', '无', '[\"这是一条宽阔的青石板街道，向南北两头延伸。南边就是朱雀门，你走在一条繁忙的街道上，看着操着南腔北调的人们行色匆匆。\"]', '1', '5', '0', '34', '72', '1');
INSERT INTO `maps` VALUES ('44', '洞房', '无', '[\"这里是洞房，可以在这里和情投意合的心上人结为夫妻，白头偕老。\"]', '3', '0', '0', '0', '6', '0');
INSERT INTO `maps` VALUES ('45', '冥思房', '无', '[\"一间静悄悄的屋子，在这里你可以冥思武学的精要，学武贵在持之以恒。旁边有一个书架，上面放着不少书。\"]', '3', '7', '0', '0', '0', '0');
INSERT INTO `maps` VALUES ('46', '镜湖(挣钱)', '无', '[\"镜湖，方圆有数里。碧水清澈，风光妩媚。明月之夜，皎洁的月光映照湖面，风拂水波，摇曳生姿，如繁星落入湖中，晶亮如云。\"]', '3', '47', '48', '49', '8', '0');
INSERT INTO `maps` VALUES ('47', '当铺(存物品)', '无', '[\"这是一家以买卖公平著称的当铺，一个五尺高的柜台挡在你的面前，柜台上摆着一个牌子，柜台后坐的是老板，一双精明的眼睛上上下下打量着你。\"]', '3', '0', '0', '0', '46', '0');
INSERT INTO `maps` VALUES ('48', '商店(买东西)', '无', '[\"这是一个古色古香的民间商店。房内摆设着上好的香檀木家具，柜子里陈列着各种商品，连墙上也挂满了物品。\"]', '3', '0', '0', '46', '0', '0');
INSERT INTO `maps` VALUES ('49', '钱庄(存取钱)', '无', '[\"这是一家老字号的钱庄，已有几百年的历史，在全国各地都有分店。它发行的银票信誉非常好，通行全国。不管你走到哪里，在任何一个本庄分店都可以任意存取。墙上挂着一个牌子。\"]', '3', '0', '46', '0', '0', '0');
INSERT INTO `maps` VALUES ('50', '武馆(学艺)', '无', '[\"钟灵武馆是专为初出江湖的少年设立的。由于江湖叛师是十分为人不齿的，初出道的少年可以在此学习一些基本的防身武功，一来不会光阴虚度，二来也好为今后选定一个心慕的门派。墙上贴着个贴子。\"]', '3', '0', '8', '51', '0', '0');
INSERT INTO `maps` VALUES ('51', '桃园', '无', '[\"眼前豁然开朗，你轻松地走在桃园边的小路上。路上落英缤纷，一边是桃树林，盛开着粉红的桃花，红云一片，望不到边。还可以看见蜜蜂「嗡嗡」地在花间飞上飞下忙个不停，这里可以与肝胆相照的好友结拜为义兄弟，义兄弟最多只能有五位。\"]', '3', '0', '50', '0', '0', '0');
INSERT INTO `maps` VALUES ('52', '夺宝擂台(PK场)', '无', '[\"这是一个四丈见方的擂台。粗壮结实的木柱撑起一片平台，四角的支柱上高挂着四副对联，四面的观众都能清楚地看到台上的手起脚落。擂台角上有个兵器架，刀剑鞭棒样样俱全。\"]', '3', '8', '0', '0', '62', '1');
