INSERT INTO `maps` VALUES ('62', '天龙八部群芳楼', '无', '[\"这里有最贵的酒，也是最好的酒，这里有最贵的女人，也是最有味儿的女人，不过这里的女人不爱金钱，只爱英雄。\"]', '3', '52', '0', '0', '63', '1');
INSERT INTO `maps` VALUES ('63', '不眠阁', '无', '[\"厅中一大台，酒肴已设。佳肴异果，罗列满案。阵阵娇笑之声从房间传来，不时还加杂着一两声令人血脉喷张的呻吟。\"]', '3', '62', '0', '0', '64', '1');
INSERT INTO `maps` VALUES ('64', '湖上花', '无', '[\"湖上花，天水浸灵芽。浅蕊水边匀玉粉，浓苞天外剪明霞，日在列仙家。\"]', '3', '63', '0', '0', '65', '1');
INSERT INTO `maps` VALUES ('65', '湖上草', '无', '[\"湖上草，碧翠浪通津。修带不为歌舞缓，浓铺堪作醉人茵，无意衬香衾。\"]', '3', '64', '0', '0', '66', '1');
INSERT INTO `maps` VALUES ('66', '湖上女', '无', '[\"湖上女，精选正轻盈。犹恨乍离金殿侣，相将尽是采莲人，清唱谩频频。\"]', '3', '65', '0', '0', '67', '1');
INSERT INTO `maps` VALUES ('67', '湖上酒', '无', '[\"湖上酒，终日助清欢。檀板轻声银甲缓，醅浮香米玉蛆寒，醉眼暗相看。\"]', '3', '66', '0', '0', '68', '1');
INSERT INTO `maps` VALUES ('68', '湖上月', '无', '[\"湖上月，偏照列仙家。水浸寒光铺枕簟，浪摇晴影走金蛇，偏称泛灵槎。\"]', '3', '67', '0', '0', '69', '1');
INSERT INTO `maps` VALUES ('69', '湖上雪', '无', '[\"湖上雪，风急堕还多。轻片有时敲竹户，素华无韵入澄波，望外玉相磨。\"]', '3', '68', '0', '0', '70', '1');
INSERT INTO `maps` VALUES ('70', '湖上水', '无', '[\"湖上水，流绕禁园中。斜日暖摇清翠动，落花香暖众纹红，苹末起清风。\"]', '3', '69', '0', '0', '71', '1');
INSERT INTO `maps` VALUES ('71', '湖上柳', '无', '[\"湖上柳，烟里不胜催。宿雾洗开明媚眼，东风摇弄好腰肢，烟雨更相宜。\"]', '3', '70', '0', '0', '0', '1');
INSERT INTO `maps` VALUES ('72', '朱雀门', '无', '[\"这是长安城的南城门，城墙以条石砌基，巨砖砌身，城砖用优质粘土和白瓷土烧成，非常坚固。\"]', '1', '43', '0', '0', '73', '1');
INSERT INTO `maps` VALUES ('73', '驿道', '无', '[\"一条青石铺就的驿道由北向南延伸，顺着驿道往南就离开长安城了。\"]', '1', '72', '74', '75', '106', '1');
