INSERT INTO `maps` VALUES ('81', '草地', '无', '[\"这里是一片草地，草很深，几乎淹没了小腿。草间散布着一些奇花，微风吹过，芬芳扑鼻。你听到时而有“嗡嗡嗡”的声音，看见有一些个头奇大的白色的蜜蜂在草地上飞舞盘旋。\"]', '8', '0', '0', '82', '109', '1');
INSERT INTO `maps` VALUES ('82', '墓门', '无', '[\"你走过了草地，眼前突然了一片开阔地，突兀地耸立着一座大墓，墓上砌的石头已经班驳了，看的出来这是座古墓。墓前没有立碑，也没有任何的标记，不知道这是前朝哪位王公大臣的墓穴。墓门口被一块的大石头封闭得丝毫不透。\"]', '8', '0', '81', '0', '0', '1');
INSERT INTO `maps` VALUES ('83', '教碑', '无', '[\"这里是全真教的教碑所在地，路旁立着一块大碑。再往东走就是天下闻名的道家玄门正宗--全真教的大门了。\"]', '8', '109', '0', '0', '84', '1');
INSERT INTO `maps` VALUES ('84', '全真教大门', '无', '[\"你已走到了终南山半山腰，前面就是全真教的总部重阳宫了。殿宇依山而筑，高低错落有致。周围古木森森，翠竹成林，景色清幽。正前方黄墙碧瓦，飞檐翘檩，正中一道二丈来宽，三丈来高的朱红杉木包铜大门，气势不凡。\"]', '8', '83', '0', '0', '85', '1');
INSERT INTO `maps` VALUES ('85', '全真教广场', '无', '[\"这里是全真教第子的练武场，广场的四周设置了各式各样的练武器械，很多全真第子正在埋头苦练。由于一年一度的较武大会关系到各自的前途，众第子都日夜勤练不息。\"]', '8', '84', '86', '87', '88', '1');
INSERT INTO `maps` VALUES ('86', '天罡台', '无', '[\"全真教弟子演练天罡北斗大阵之所,地下的青砖本来铺的整整齐齐却被踩出了几十个大大小小的凹坑。看的出来，这里成年累月的有弟子演练天罡北斗大阵，天长日久，地下就踩出了凹坑。\"]', '8', '0', '0', '85', '0', '1');
INSERT INTO `maps` VALUES ('87', '休息室', '无', '[\"这是间很大的房间，光线非常昏暗。房里没有什么别的东西，只有中间放着一张收拾得舒舒服服的大床，床上的被褥也整理得整整洁洁，看着就让人想睡觉。\"]', '8', '0', '85', '0', '0', '0');
INSERT INTO `maps` VALUES ('88', '大堂一进', '无', '[\"这里是大堂，是全真教接待客人的地方。堂很大，几十张桌椅一字排开也丝毫不显拥挤。地下铺着整齐的青砖，几名道童正忙前忙后地端茶送水招呼客人。\"]', '8', '85', '89', '90', '91', '1');
INSERT INTO `maps` VALUES ('89', '显真堂', '无', '[\"这里是大堂旁的一个侧厅，也是接待重要客人的地方。厅不大，只摆放着一张桌子和几把椅子。桌上摆放着一盆青松山石的盆景\"]', '8', '0', '0', '88', '0', '1');
INSERT INTO `maps` VALUES ('90', '会真堂', '无', '[\"这里是大堂旁的一个侧厅，也是接待重要客人的地方。厅不大，只摆放着一张桌子和几把椅子。桌上摆放着一盆青松山石的盆景，墙壁上挂着一些字画。\"]', '8', '0', '88', '0', '0', '1');
INSERT INTO `maps` VALUES ('91', '大堂二进', '无', '[\"这里是大堂的第二重，是全真教弟子做功课的所在。现在还不到功课时间，大堂里空荡荡的，只有全真教掌管上下杂事的掌理道长带领着几个小道童在整理桌椅，预备餐饭。\"]', '8', '88', '92', '93', '94', '1');
INSERT INTO `maps` VALUES ('92', '过真殿', '无', '[\"这是大堂旁边的一个侧殿，也是接待重要客人的地方。殿不大，只摆放着一张桌子和几把椅子。桌上摆放着一盆青山隐隐的盆景，墙壁上挂着一些字画。从这里往北有座小塔。\"]', '8', '0', '0', '91', '0', '1');
INSERT INTO `maps` VALUES ('93', '乃容殿', '无', '[\"这里是大堂旁边的侧殿，也是接待重要客人的地方。殿不大，只摆放着一张桌子和几把椅子。桌上摆放着一盆青山隐隐的盆景，墙壁上挂着一些字画。\"]', '8', '0', '91', '0', '0', '1');
INSERT INTO `maps` VALUES ('94', '老君殿', '无', '[\"这里是供奉太上老君的大殿。殿中塑着老君的塑像，老君骑在青牛上，正临云而去。两旁塑着两个童子，栩栩如生。殿中的地都是以青砖一块块的铺成，打扫得一尘不染。\"]', '8', '91', '95', '96', '97', '1');
INSERT INTO `maps` VALUES ('95', '元始殿', '无', '[\"这里是供奉元始天尊的大殿。殿内香烟缭绕，正中央塑着元始天尊的金身，旁边还塑着几位道友如太乙、俱留孙、云中子、广成子等等的像，正聚在一处或躺或坐，或棋或茶。\"]', '8', '0', '0', '94', '0', '1');
INSERT INTO `maps` VALUES ('96', '通天殿', '无', '[\"这里是供奉三清之末--通天教主的大殿，殿内的香烟不甚旺盛。在通天教主的金身旁边，塑着几位截教道友，象长耳仙、归云仙、申公豹的塑像。\"]', '8', '0', '94', '0', '0', '1');
INSERT INTO `maps` VALUES ('97', '后堂一进', '无', '[\"这里是后堂的一进，是平常全真教弟子休息的地方。由于全真教现在广收弟子，堂中摆放的桌椅也不断增加，显得这个堂已经很拥挤了。\"]', '8', '94', '98', '99', '100', '1');
INSERT INTO `maps` VALUES ('98', '广宁居', '无', '[\"这里是全真七子之六郝大通的卧室，卧室很小，除了一张小床，一张小茶几而外没有什么别的摆设。茶几晶萤透明，仔细一看，原来几面是用整块的玉琢磨成的。茶几上摆放着几本先天课术的书，还有一些卜卦用的金钱。\"]', '8', '0', '0', '97', '0', '1');
INSERT INTO `maps` VALUES ('99', '事为室', '无', '[\"这里是全真七子之首马钰的居室。房间很小，只摆放一张床、一张书桌和一把椅子。书桌上放着本老子，床上被褥折叠得整整齐齐。\"]', '8', '0', '97', '0', '0', '1');
INSERT INTO `maps` VALUES ('100', '后堂二进', '无', '[\"这里是全真教弟子平常研讨武功的地方，这个堂不大，由于经常要在这里演武，索性连桌椅也全都靠到墙边去了，这样腾出了一大片空地来。\"]', '8', '97', '101', '102', '103', '1');
INSERT INTO `maps` VALUES ('101', '万物堂', '无', '[\"这里是全真七子之五王处一的居所。房间不大，收拾的素雅整洁，墙上挂着一萧一琴和一些字画，小几上摆放着宣纸和徽墨狼毫，不知道主人是要练字还是绘画。\"]', '8', '0', '0', '100', '0', '1');
INSERT INTO `maps` VALUES ('102', '容物堂', '无', '[\"这里是全真七子之二谭处端的居室，这间房间收拾的和普通的民居没什么区别，只是一尘不染，墙上斜挂着一把宝剑。\"]', '8', '0', '100', '0', '0', '1');
INSERT INTO `maps` VALUES ('103', '后山密室', '无', '[\"重阳宫后山的密室，囚禁着四名罪恶滔天之徒\"]', '8', '100', '0', '0', '0', '1');
INSERT INTO `maps` VALUES ('104', '成都北门', '无', '[\"这是成都北城门，城门正上方刻着北门两个楷书大字，城墙上贴着几张官府告示。一条笔直的青石板大道向北延伸。北边望去，一片绿油油的田野夹着随风摇曳的高树，长竹。城门边两行官兵仔细盘查进出人等。\"]', '2', '108', '0', '0', '110', '1');
