INSERT INTO `maps` VALUES ('106', '秦岭', '无', '[\"这里是有名的秦岭，崇山峻岭不断。\"]', '2', '73', '0', '0', '107', '1');
INSERT INTO `maps` VALUES ('107', '驿道', '无', '[\"一条驿道静静的向远方延伸。\"]', '2', '106', '0', '109', '108', '1');
INSERT INTO `maps` VALUES ('108', '驿道', '无', '[\"你走在一条青石大道上，人来人往非常繁忙，不时地有人骑着马匆匆而过。\"]', '2', '107', '0', '0', '104', '1');
INSERT INTO `maps` VALUES ('109', '终南山脚', '无', '[\"这里是终南山的山脚，终南山号称西北的第一山，以山势奇峻，环境清幽著称。这里的山势还很平缓，一道石阶蜿蜒通往山上。面前是一座高山，如同擎天一柱般插向天空，所以古人的诗句中有「终南一柱」的说法。\"]', '8', '81', '107', '0', '83', '1');
INSERT INTO `maps` VALUES ('110', '北大街', '无', '[\"一条青石铺成的大道通向南北。路上行人络绎不绝，只见南面不远处的古树下有一个简陋的摊子，摊子一角的招牌在风中摇晃着，上面写着“诸葛再世”，居然也围了一堆人。\"]', '2', '104', '111', '112', '114', '1');
INSERT INTO `maps` VALUES ('111', '听雨居', '无', '[\"这是成都市内最有名气的一家客栈，当年徐子陵入川之时，曾与仙子师妃暄在此相聚。\"]', '2', '0', '0', '110', '0', '0');
INSERT INTO `maps` VALUES ('112', '散花楼', '无', '[\"这是一座有着三开间门面的大酒楼，虽说不上雕梁画栋却也气派不凡。正首门梁上挂着一幅黑漆匾，上写着蜀都酒楼四个金漆大字。\"]', '2', '0', '110', '0', '113', '1');
INSERT INTO `maps` VALUES ('113', '东大街', '无', '[\"你走在东大街上，踩着坚实的青石板地面。向东是一条宽直的大道。东边可以通往东城门，西边通向市中心，北面便是有名的散户楼。\"]', '2', '112', '114', '116', '120', '1');
INSERT INTO `maps` VALUES ('114', '市中心', '无', '[\"这儿就是成都的市中心了，来来往往的行人不停的经过你身边。一些花花公子，阔少恶霸带着家奴横行霸道，也有一些卖艺人在辛苦地卖艺。\"]', '2', '110', '115', '113', '117', '1');
INSERT INTO `maps` VALUES ('115', '西大街', '无', '[\"这里一条北大街横贯东西。望向西边，是出城的西门。号称 天下幽 的青城山，就是从这里出去。这里地当当道，行人却都过往匆匆，小心翼翼，恨不得销声匿迹。因为南边一幢古色古香的建筑，就是让天下武林闻风色变的川中唐门。\"]', '2', '0', '118', '114', '119', '1');
INSERT INTO `maps` VALUES ('116', '东大街', '无', '[\"你走在东大街坚实的青石板地面上。南边可以通向东城门路边有一个简陋的茶摊，一块洗得发白了的旧帆布正顶着头顶火辣辣的阳光，一张长几上放着几只大号粗瓷碗。碗中盛满着可口的茶水。\"]', '2', '121', '113', '124', '122', '1');
INSERT INTO `maps` VALUES ('117', '南大街', '无', '[\"你走在南大街坚实的青石板地面上，各色人等往来其中很是喧闹。有时你可能会见到一些江湖装束的人物，他们口音各异，服饰也五花八门，可是中气都很足，可不是好惹的。\"]', '2', '114', '0', '0', '125', '1');
INSERT INTO `maps` VALUES ('118', '成都西门', '无', '[\"你来到了西城门外，只见高大的城墙厚实凝重，卫兵仔细地盘查着过往行人，一切都显得井井有条。城墙上贴着几张告示。西面远远可以看到城外的清溪碧水。\"]', '2', '0', '134', '115', '0', '1');
INSERT INTO `maps` VALUES ('119', '唐门', '无', '[\"你抬头一望，赫然见到“唐门” 俩个大字。你已经来到了神秘而声名显赫声名显赫的唐门。高大的门让你不由得不仰视，大门口是两个威武雄壮的铜狮，两旁是笔力遒劲的对联《百年世家传千古风流，一腔热血可快意恩仇》\"]', '2', '115', '0', '0', '841', '1');
INSERT INTO `maps` VALUES ('120', '天府钱庄', '无', '[\"这是一家老字号的钱庄，已有几百年的历史，在全国各地都有分店。它发行的银票信誉非常好，通行全国。\"]', '2', '113', '0', '0', '0', '1');
INSERT INTO `maps` VALUES ('121', '成都药铺', '无', '[\"这是一家药铺，一股浓浓的药味让你几欲窒息，那是从药柜上的几百个小抽屉里散发出来的。一名小伙计站在柜台后招呼着顾客。柜台上贴着一张发黄的广告。\"]', '2', '0', '0', '0', '116', '0');
INSERT INTO `maps` VALUES ('122', '唐记当铺', '无', '[\"这里是扬州城内一家以买卖公平著称的当铺，一个五尺高的柜台挡在你的面前，柜台上摆着一个牌子 ，柜台后坐着一个伙计，一双精明的上上下下打量着你。\"]', '2', '116', '0', '0', '0', '1');
INSERT INTO `maps` VALUES ('124', '成都东门', '无', '[\"这是成都东城门，城门正上方刻着“东门”两个楷书大字城墙上贴着几张官府告示。一条笔直的青石板大道向东西两边延伸。东边是郊外，一片平平展展的平原风光，真是不愧天府之国的美称。西边是城里。\"]', '2', '0', '116', '215', '0', '1');
INSERT INTO `maps` VALUES ('125', '成都南门', '无', '[\"这是成都南城门，城门正上方刻着“南门”两个楷书大字城墙上贴着几张官府告示。一条笔直的青石板大道向南延伸。南边是郊外，遍地麦浪金黄迷人。北边是城里。\"]', '2', '117', '0', '0', '126', '1');
INSERT INTO `maps` VALUES ('126', '驿道', '无', '[\"一条驿道静静的向远方延伸，好象永远没有尽头。\"]', '2', '125', '127', '0', '128', '1');
INSERT INTO `maps` VALUES ('127', '大雪山山路', '无', '[\"远处可以看见终年积雪的山尖。\"]', '14', '558', '585', '126', '0', '1');
INSERT INTO `maps` VALUES ('128', '驿道', '无', '[\"一条驿道静静的向远方延伸，好象永远没有尽头。\"]', '12', '126', '129', '411', '0', '1');
INSERT INTO `maps` VALUES ('129', '大理东门', '无', '[\"这是东城门，城门正上方刻着“东门”两个楷书大字，官兵们警惕地注视着过往行人，你最好小心为妙。\"]', '12', '0', '415', '128', '0', '1');
INSERT INTO `maps` VALUES ('130', '驿道', '无', '[\"你走在一条青石大道上，人渐渐少起来，只是有时有人骑着马匆匆而过。\"]', '10', '132', '128', '411', '131', '1');
INSERT INTO `maps` VALUES ('131', '驿道', '无', '[\"一条驿道静静的向远方延伸，好象永远没有尽头。\"]', '12', '130', '0', '0', '0', '1');
INSERT INTO `maps` VALUES ('132', '驿道', '无', '[\"你走在一条青石大道上，人来人往非常繁忙，不时地有人骑着马匆匆而过。\"]', '10', '133', '0', '0', '130', '1');
INSERT INTO `maps` VALUES ('133', '广州南门', '无', '[\"这是南城门，城墙上贴着官府的告示。\"]', '10', '0', '0', '0', '132', '1');
INSERT INTO `maps` VALUES ('134', '驿道', '无', '[\"你走在一条青石大道上，人来人往非常繁忙，不时地有人骑着马匆匆而过。\"]', '2', '135', '182', '118', '136', '1');
INSERT INTO `maps` VALUES ('135', '青城山', '无', '[\"你走在青城山路上。山木蔽日，幽静宜人。\"]', '5', '137', '0', '0', '134', '1');
INSERT INTO `maps` VALUES ('136', '峨嵋山', '无', '[\"峨眉山秀甲天下，众多的寺庙香火，林立于高山峻峰之上的古剎古屋，一座座翠山碧峰，无不吸引着无数朝圣信徒和观光游客。天造峨眉，匠心独运；奇峰异岭耸西南，秀甲天下名自远。\"]', '6', '134', '162', '0', '0', '1');
INSERT INTO `maps` VALUES ('137', '建福宫', '无', '[\"建福宫旧称丈人观，以其座落丈人峰下故也。相传五岳丈人宁封子修道于此。院内树木假山，相互掩映；周围林木苍翠，浓荫蔽日，炎夏来此，十分凉爽。这里也是上山的起点。\"]', '5', '138', '0', '0', '135', '1');
INSERT INTO `maps` VALUES ('138', '盘山磴道', '无', '[\"你走在盘山磴道上。山木蔽日，幽静宜人。过了“青城”木牌楼，西上就是天然图画了。东南方向是建福宫。\"]', '5', '141', '139', '140', '137', '1');
INSERT INTO `maps` VALUES ('139', '飞仙亭', '无', '[\"沿石梯向下，就是海棠溪。这里是溪上的飞仙亭，四顾山环水绕，古藤蔓蔓，飞泉溅落池中，如琴弦轻拨，如珠落玉盘，清绝幽绝。\"]', '5', '0', '0', '138', '0', '1');
INSERT INTO `maps` VALUES ('140', '山神庙', '无', '[\"庙有一大半倾塌了，本来就不大，现在就显得更小了，不过小无减于它的慑人气氛。没有倾塌的是神殿的一角，而且刚好是神像所在的地方，所以那尊土塑的山神还算是完好的。这也不知是比照哪一位尊神所塑的像？青面潦牙，眼睛瞪得像两枚铜铃，而且还熠熠发光。\"]', '5', '0', '138', '0', '0', '1');
INSERT INTO `maps` VALUES ('141', '天然图画', '无', '[\"这里岩壁矗立，绿树映辉，牌阁在山垭间巍然屹立。四顾峰奇石异，云雾缥缈，远近观赏，如在画中。阁左“天生桥”，一石丈余飞插石壁之间，十分险峻。\"]', '5', '144', '142', '143', '138', '1');
INSERT INTO `maps` VALUES ('142', '八卦台', '无', '[\"它布局紧凑，精巧大方，整体建筑共三层，建于重台之上，亭座石台基呈四方形，亭身呈圆形，象征古代天圆地方之说；两重飞檐鸱吻，四周有龟纹隔门和云花镂窗，南向正门是十二属相太极图的浮雕，造型古朴典雅\"]', '5', '0', '0', '141', '0', '1');
INSERT INTO `maps` VALUES ('143', '鬼城山', '无', '[\"传为岷山真人鬼谷子(即蜀中八仙之首容成公，传为黄帝师)隐居处。\"]', '5', '0', '141', '0', '0', '1');
INSERT INTO `maps` VALUES ('144', '龙隐栈道', '无', '[\"龙隐峡栈道修于半山上，紧靠峭崖，旁临深壑，蜿蜒曲折，绝壁处还凌空架板通行，是个观山赏景，避暑探幽的好地方。\"]', '5', '147', '145', '146', '141', '1');
INSERT INTO `maps` VALUES ('145', '五龙沟', '无', '[\"五龙沟，古称蛮河，因传说古时有五条神龙，隐于沟中而得名。五龙沟里，杜鹃、山茶、山桂、野菊等点缀其间，有如神奇壮丽的仙山佳境。\"]', '5', '0', '0', '144', '0', '1');
INSERT INTO `maps` VALUES ('146', '丈人峰', '无', '[\"丈人峰，形状好像老翁伛偻着背而得名。附近又有数块稍小之石相配，因此又有“老翁弄孙”之称。\"]', '5', '0', '144', '0', '0', '1');
INSERT INTO `maps` VALUES ('147', '天师洞', '无', '[\"天师洞以东汉张陵在此讲道修炼而得名，又名古常道观。洞在山腰混元顶下峭壁间。中塑天师像，沿岩壁有廊可通。正殿名三皇殿，重檐回廊，雄锯高台，气势宏伟。内供伏羲、神农、轩辕三皇石刻造像。\"]', '5', '150', '148', '149', '144', '1');
INSERT INTO `maps` VALUES ('148', '古银杏树', '无', '[\"观前古银杏树相传为张天师手植，高可十余丈，腰围七、八人合抱，枝叶扶疏，苍盖如云。\"]', '5', '0', '0', '147', '0', '1');
INSERT INTO `maps` VALUES ('149', '三岛石', '无', '[\"三岛石上刻“降魔”二字，相传为张天师当年降魔，因巨石当道，挥剑力劈，石裂为三，成为三块相连的岛石。石裂处自然生成石梯\"]', '5', '0', '147', '0', '0', '1');
INSERT INTO `maps` VALUES ('150', '飞泉沟', '无', '[\"飞泉沟古名冷凝沟，由泰安寺过黄鹤桥即至。水源出蓥华山南天门，纳白云洞古磨沟、观音岩沟、响水沟，流入味江。其中又数“幽谷飞泉”、“百丈长桥”、“双泉水帘”最为壮观。\"]', '5', '153', '151', '152', '147', '1');
INSERT INTO `maps` VALUES ('151', '熊耳山', '无', '[\"熊耳山像一只大白熊，头东尾西地横卧着；主峰像一扇张开的熊耳，故名熊耳山。\"]', '5', '0', '0', '150', '0', '1');
INSERT INTO `maps` VALUES ('152', '四望台', '无', '[\"台山可以环顾四方，欣赏美景\"]', '5', '0', '150', '0', '0', '1');
INSERT INTO `maps` VALUES ('153', '观日亭', '无', '[\"这里是青城山上的观日亭，不知哪朝哪代何人在这里修筑了这个平台，用来观瞻日出的美景。这里的山势非常陡峻，往下一看，悬崖壁立，脚下白云飘过，真不知身在何境。\"]', '5', '156', '154', '155', '150', '1');
INSERT INTO `maps` VALUES ('154', '百丈桥', '无', '[\"百丈桥位于翠映湖与白云古寨之间，飞泉沟中游，用木板铺成曲桥，逆水而上，长约百余米。两岸老树龙钟，附生着草绿色的“木萝莎”如薄纱飘舞。翠映湖：翠映湖是几十米长的山间水潭，周围景色秀丽，\"]', '5', '0', '0', '153', '0', '1');
INSERT INTO `maps` VALUES ('155', '云海亭', '无', '[\"从亭上远眺，只见云海苍茫处，险绝万峦生，青城一脉，逶迤千里，真一派大好江山。云海亭立于青城山峰，足下悬崖峭壁，深渊万丈。\"]', '5', '0', '153', '0', '0', '1');
INSERT INTO `maps` VALUES ('156', '上清宫', '无', '[\"上清宫是老君道场。宫前山门，矮树搭就，甚为灵动。宫内殿堂古色清雅，正殿供奉太上老君、吕纯阳和张三丰塑像。山前摩崖石刻 ”天下第五名山“ 气势浑厚，很是壮观。\"]', '5', '159', '157', '158', '153', '1');
INSERT INTO `maps` VALUES ('157', '鸳鸯井', '无', '[\"上清殿左有鸳鸯井，一方一圆，泉流暗通。圆者水浊，方者水清。井水一温一凉，昔人题诗曰：“谁知上界神仙客，也与炎凉世态同”。\"]', '5', '0', '0', '156', '0', '1');
INSERT INTO `maps` VALUES ('158', '松风观', '无', '[\"这里是松风观，是青城派总所。青城派威镇川中，以其人才辈出之故。当今掌门人余沧海道长，虽其貌不扬，身形矮小，却是青城一代宗师。观中陈设奢华，显见青城派财力不菲。\"]', '5', '0', '156', '0', '0', '1');
INSERT INTO `maps` VALUES ('159', '呼应亭', '无', '[\"呼应亭高居彭祖峰顶，是青城最高处。登临长啸，余音回荡，众山皆应。看雪岭光腾，红吞沧海，锦江春涨，绿到瀛洲。夏日夜分，有圣灯浮云，传说是神仙朝贺张天师所点仙灯，看来让人感叹不已。\"]', '5', '0', '160', '161', '156', '1');
INSERT INTO `maps` VALUES ('160', '朝阳洞', '无', '[\"你来到朝阳洞。洞深广数丈，相传是宁封子栖息处。这里也是观日好去处，“古洞常临光照耀”，正是指旭日东升，霞光透过绿荫，射到洞口，一片璀灿绚丽的景色。\"]', '5', '0', '0', '159', '0', '1');
INSERT INTO `maps` VALUES ('161', '悬崖底', '无', '[\"这里是青城山悬崖底，四周都被悬崖绝壁包围着，举目一望，只见云雾深锁，崖壁陡峭，直没天际，几只老鹰在上空不断的盘旋。绝壁上垂下几根树藤，看来可以顺着这里爬上崖顶。耸立在你面前是一棵巨大的古树，刚才你幸好抓住了这棵树的树枝，不然早就粉身碎骨了。你到处看了看，这里似乎没有其他路可走。\"]', '5', '0', '159', '0', '0', '1');
INSERT INTO `maps` VALUES ('162', '报国寺山门', '无', '[\"报国寺为峨嵋出入之门户。山门上有一块大匾。寺外苍楠环卫，翠柏掩映。由此一路向西上开始登峨嵋山，北面下山是一条弯弯的石阶路。\"]', '6', '163', '0', '136', '0', '1');
INSERT INTO `maps` VALUES ('163', '报国寺', '无', '[\"报国寺是峨眉山最的大寺院之一。寺庙倚山建造，弥勒殿大雄殿、七佛殿逐级升高，规模宏大。殿内佛像金光闪闪气宇轩昂。寺内芳草迷径，花开不绝。这里有座华严塔。这里向西，约二里可达伏虎寺。\"]', '6', '164', '0', '0', '162', '1');
INSERT INTO `maps` VALUES ('164', '解脱坡', '无', '[\"这里是峨嵋胜地解脱坡。坡下一座小桥，叫解脱桥。行人至此，坐桥上听泉声甚美。当此良辰美景，心无芥蒂，心即解脱。坡上一座庵堂，东下一片密林。桥下山溪向东流淌到报国寺的西面。\"]', '6', '167', '165', '166', '163', '1');
INSERT INTO `maps` VALUES ('165', '观音堂', '无', '[\"观音堂高踞坡岗近临危崖，环境幽静，四周树木蓊郁，悦目赏心。传说入山于此，解脱尘凡；出山于此，解脱险阻，又故名解脱庵。进山的人在此焚香洗心，入山方可化险为夷步步平安。这里东下可达伏虎寺，西上行约二里可到归云阁。\"]', '6', '0', '0', '164', '0', '1');
INSERT INTO `maps` VALUES ('166', '归云阁', '无', '[\"归云阁，原是唐代福昌达道禅师的道场。宋代绍兴年间僧人士性又率众重建。归云阁左有玉女峰，西上可抵纯阳殿，东下是观音堂。\"]', '6', '0', '164', '0', '0', '1');
INSERT INTO `maps` VALUES ('167', '纯阳殿', '无', '[\"纯阳殿海拔一千余米，四面古木苍翠，幽静宜人。殿前有一石，叫普贤石，据说普贤菩萨登山时曾坐在这里休息过。在此可纵观峨嵋诸峰，仰望金顶，悬崖峭壁，离天咫尺。\"]', '6', '170', '168', '169', '164', '1');
INSERT INTO `maps` VALUES ('168', '福寿庵', '无', '[\"福寿庵在神水庵南，是一个名叫天性的僧人建造的。庵前有九曲渠、流杯池，古人曾据在此饮酒流杯观景赋诗，歌咏兴叹不知夕日。庵旁有株海棠树，数百苍龄高达十余丈。\"]', '6', '0', '0', '167', '0', '1');
INSERT INTO `maps` VALUES ('169', '灵文阁', '无', '[\"这里是福寿庵的灵文阁，是主持静照师太传授佛法的地方。两名青衣小尼姑随侍在旁，似乎在聆听师太的教晦。阁内布置简陋，靠墙放着几把竹椅，地上放着几个蒲团。\"]', '6', '0', '167', '0', '0', '1');
INSERT INTO `maps` VALUES ('170', '中峰寺', '无', '[\"中峰寺在白云峰下，始建于晋代，原为道观。传说观内的道人每年在三月三日都献身给山中一条大蟒，以为可升仙。后来有个从资州来的明果禅师，杀了那条大蟒，道士们始知上当，感激不已，自愿改信佛教，从此改观为寺。\"]', '6', '173', '171', '172', '167', '1');
INSERT INTO `maps` VALUES ('171', '清音阁', '无', '[\"这里是峨嵋山的清音阁。此阁位于牛心岭下，东有白龙江西有黑龙江，两江之水交汇于清音阁下。江上有拱桥两座，分夸黑龙白龙二江，这就是双飞桥。两江合流处，有一数米高的黑褐色巨石傲然挺立，形似牛心，名之为「牛心石」。\"]', '6', '0', '0', '170', '0', '1');
INSERT INTO `maps` VALUES ('172', '神水庵', '无', '[\"神水庵位于宝掌峰下，庵前有大峨石和玉液泉，泉出石下清澈无比，终年不涸。一株大海棠树已是数百苍龄，抱可数围高达十余丈。旁边有福寿庵；由此西上中峰寺东下纯阳殿。\"]', '6', '0', '170', '0', '0', '1');
INSERT INTO `maps` VALUES ('173', '华严顶', '无', '[\"华严顶挺拔高峭，古刹云深，殿宇孤耸，风景奇险。这里视野开阔，仰可见金顶巍巍，俯可望群峰罗列，许多蹲伏山间的寺院也在眼底。\"]', '6', '176', '174', '175', '170', '1');
INSERT INTO `maps` VALUES ('174', '凌云梯', '无', '[\"这是凌云梯，最早是一位叫阿婆多的外国和尚在此缚木架石，以渡行人，又叫「胡僧梯」。路边有梅子坡，可见满坡青梅如豆，于疏枝密叶中逗引行人，甚有望梅止渴之功用。\"]', '6', '0', '0', '173', '0', '1');
INSERT INTO `maps` VALUES ('175', '千佛庵', '无', '[\"此处是洪椿坪千佛庵。山间小坪坝，四面层峦叠障，峰环岭护；两侧深谷幽岚，溪涧玲叮。坪上古木扶疏，亭亭如盖山阴沁透，白雾飘游，极为幽静清凉。此处常见雨雾霏霏，林中迷迷蒙蒙，乃山中雾气所为。正所谓「山行本无雨，空翠湿人衣」。这就是誉为奇观的「洪椿晓雨」。此处殿美景幽，气象新异，令人心醉。\"]', '6', '0', '173', '0', '0', '1');
INSERT INTO `maps` VALUES ('176', '接引殿', '无', '[\"接引殿右边濒崖，是金钢嘴。崖下有一块巨石，形状似钟故名「钟石」。对面有一石，高耸十余丈，叫仙人石。由此向上仰攀即到万行庵，北下经「八十四盘」至洗象池。\"]', '6', '179', '177', '178', '173', '1');
INSERT INTO `maps` VALUES ('177', '雷洞坪', '无', '[\"雷洞坪一带道路艰险骇人，路左是蔽日遮天的冷杉林，路右是深不见底的绝壁峡谷。这里长年云遮雾罩空蒙黑暗。岩上建有雷神殿，旁边竖一铁碑。\"]', '6', '0', '0', '176', '0', '1');
INSERT INTO `maps` VALUES ('178', '万行庵', '无', '[\"太子坪万行庵是古智禅师创建。庵外满山长满莎椤花，花数芭合成一朵，叶包在花外，古人以其根坚难易而盛赞，以喻洁身自好之高贵品质。\"]', '6', '0', '176', '0', '0', '1');
INSERT INTO `maps` VALUES ('179', '金顶', '无', '[\"这就是峨嵋山的主峰金顶。此峰耸立大地，高入云表，秀丽而又壮美，李白赞颂它：『青冥倚天开，彩错疑画出』登上金顶，视野豁然开朗，鸟看脚下，但见群峰涌绿叠翠，三江如丝如带；回首远眺，则见大雪山横亘天际，贡嘎山直插苍穹。\"]', '6', '0', '180', '181', '176', '1');
INSERT INTO `maps` VALUES ('180', '华藏庵', '无', '[\"华藏庵是金顶的主要建筑，规模宏大，中祀普贤菩萨，旁列万佛。华藏庵后边是睹光台，旁边是卧云庵。\"]', '6', '0', '0', '179', '0', '1');
INSERT INTO `maps` VALUES ('181', '卧云庵', '无', '[\"卧云庵旁边有个井络泉，据说以前曾因饮水人多了而干涸，众尼为之诵经，于是泉水复出。出了卧云庵便是睹光台，旁边便是华藏庵。\"]', '6', '0', '179', '0', '0', '1');
INSERT INTO `maps` VALUES ('182', '驿道', '无', '[\"一条驿道静静的向远方延伸。\"]', '28', '183', '184', '134', '0', '1');
INSERT INTO `maps` VALUES ('183', '百花谷', '无', '[\"一个美丽的山谷，开满了各式各样的花,花香四溢，有如仙境\"]', '28', '0', '0', '0', '182', '1');
INSERT INTO `maps` VALUES ('184', '驿道', '无', '[\"一条驿道静静的向远方延伸。\"]', '28', '0', '185', '182', '0', '1');
INSERT INTO `maps` VALUES ('185', '驿道', '无', '[\"一条驿道静静的向远方延伸。\"]', '28', '0', '186', '184', '0', '1');
INSERT INTO `maps` VALUES ('186', '驿道', '无', '[\"一条驿道静静的向远方延伸。\"]', '28', '0', '187', '185', '0', '1');
INSERT INTO `maps` VALUES ('187', '拉萨', '无', '[\"青藏高原上的神秘之城，在蓝天白云的映衬下格外辉煌，而且很是壮观。也是藏族人民最神圣的地方，很多虔诚的信徒。\"]', '28', '188', '0', '186', '0', '1');
INSERT INTO `maps` VALUES ('188', '戈壁', '无', '[\"眼前是一望无际的戈壁滩，没有路标，也没有一个行人，天显得很高，远山披雪，苍鹰翱翔，走在这茫茫戈壁之上，你似乎迷路了。\"]', '28', '189', '0', '0', '187', '1');
INSERT INTO `maps` VALUES ('189', '昆仑山下', '无', '[\"这里是千里昆仑的东麓，远望苍天浩浩，白云悠悠。东方沃野千里，楚天辽阔；西面高山巍巍，迤逦万里。叱咤武林八百年的明教就座落在这群山之中。明教----是多少武林豪杰所向往的地方，在那里，有无数生死相许的好兄弟，无论是正是邪。就在这西方的群山之后，等待着你去谱写又一曲武林传奇。\"]', '34', '190', '0', '0', '188', '1');
INSERT INTO `maps` VALUES ('190', '明教山门', '无', '[\"一个巨大的天然石桥横跨在两片山壁之间，石桥正上方龙盘凤舞刻着两个大字：『明教』。字迹有些剥落，看上去年代久远。三位三十来岁的大汉手持钢剑守在山门两侧。\"]', '34', '192', '0', '0', '189', '1');
INSERT INTO `maps` VALUES ('191', '天字门', '无', '[\"这里就是明教的“天字门”了，这里是明教中男弟子修炼的地方，从门口看进去，汗衫扬臭，木桌流酒。此处不允女人随意进入。\"]', '34', '0', '0', '192', '0', '1');
INSERT INTO `maps` VALUES ('192', '青石阶', '无', '[\"这里已是半山腰了。不远的前面有一座竹亭，几位剑客在这里歇息。山路到这里更加陡峭，四下望去，层峦迭嶂，满目滴翠远山披银装，云海现奇观。\"]', '34', '194', '191', '193', '190', '1');
INSERT INTO `maps` VALUES ('193', '地字门', '无', '[\"这里就是明教的“地字门”了，这里是明教中女弟子修炼的地方，从门口看进去，绫罗幔影，身姿绰绰。虽是江湖儿女，却也不让男子随意进入。\"]', '34', '0', '192', '0', '0', '1');
INSERT INTO `maps` VALUES ('194', '青石阶', '无', '[\"石级到这里似乎变得宽阔了些。清新的山风扑面而来，令人精神顿为一爽。远处传来淙淙水声，渐行渐远。山壁上挂满了厚密的藤萝，随风轻轻摇弋\"]', '34', '197', '195', '196', '192', '1');
INSERT INTO `maps` VALUES ('195', '雷字门', '无', '[\"这里就是明教的“雷字门”了，这里是明教中西域弟子修炼的地方。西域风俗大异中土，他们的生活习惯以及使用物品也与中土迥异。\"]', '34', '0', '0', '194', '0', '1');
INSERT INTO `maps` VALUES ('196', '风字门', '无', '[\"这是竹林中的一间小院，竹影依依，经声入耳，其南北各有一间竹舍，乃是明教中释道和佛家弟子的修炼场所。\"]', '34', '0', '194', '0', '0', '1');
INSERT INTO `maps` VALUES ('197', '青石大道', '无', '[\"这里大道平坦，地势开阔，两旁树林中鸟语花香，一片和平安祥的气氛。此时又有谁能想到，这条大道上撒下过多少江湖中人的血，这片林中又埋有多少惨死于武林争斗的冤魂的骨\"]', '34', '199', '201', '202', '194', '1');
INSERT INTO `maps` VALUES ('199', '青石大道', '无', '[\"大路已经延伸到了尽头，西首方向一座门楼高耸，门前有十几个金衣人往返巡逻，气势森严。门楼两旁并插镶金青龙旗，迎风招展，烈烈生威。红色大门紧闭，不时有教众从侧小门进出，也被盘查得紧。\"]', '34', '204', '200', '203', '197', '1');
INSERT INTO `maps` VALUES ('200', '厚土旗', '无', '[\"这里是厚土旗的大厅，厅中刀枪如林，黄旗招展，教众穿着铁甲钢盔，腰间别着锹铲锤钻，一个个精神抖擞。正中端坐着个矮墩汉子，就是厚土旗掌旗使颜垣。\"]', '34', '0', '0', '199', '0', '1');
INSERT INTO `maps` VALUES ('201', '烈火旗', '无', '[\"这里是烈火旗的大厅，厅中红旗招展，刀枪映血，教众绛衣赤帽，团团拥住烈火旗掌旗使辛然。\"]', '34', '0', '0', '197', '0', '1');
INSERT INTO `maps` VALUES ('202', '巨木旗', '无', '[\"这是巨木旗的大厅，虽然都是练武之人，这里却布置得古色古香。整个大厅就建在神木之中，树木掩映，极为严密。虽然未免挤迫，却绝无外敌能轻易攻得上来。厅中充满茶香和古木幽香，十分动人。\"]', '34', '0', '197', '0', '0', '1');
INSERT INTO `maps` VALUES ('203', '洪水旗', '无', '[\"这里是洪水旗的大厅，厅中四壁漆墨，连桌案台椅俱为黑色，枪曳黑缨，剑垂玄穗，布置得冷漠森严。厅中央摆有茶几，洪水旗掌旗使唐洋正襟而坐，面带杀气。\"]', '34', '0', '199', '0', '0', '1');
INSERT INTO `maps` VALUES ('204', '锐金旗', '无', '[\"这是锐金旗的大厅，大厅正面高悬一块牌匾，上书四个烫金大字“锐气金风”，一百二十年前，明教与来犯的蒙古军队一场鏖战，锐金旗力守玄天崖，死者八、九，教主亲书此匾以励教众，锐金旗以此为五旗之首。\"]', '34', '207', '205', '206', '199', '1');
INSERT INTO `maps` VALUES ('205', '神蛇坛', '无', '[\"这里就是明教的神蛇坛。\"]', '34', '0', '0', '204', '0', '1');
INSERT INTO `maps` VALUES ('206', '青龙坛', '无', '[\"这里就是明教的青龙坛。\"]', '34', '0', '204', '0', '0', '1');
INSERT INTO `maps` VALUES ('207', '朱雀坛', '无', '[\"这里就是明教的朱雀坛。\"]', '34', '210', '208', '209', '204', '1');
INSERT INTO `maps` VALUES ('208', '白虎坛', '无', '[\"这里就是明教的白虎坛。\"]', '34', '0', '0', '207', '0', '1');
INSERT INTO `maps` VALUES ('209', '玄武坛', '无', '[\"这里就是明教的玄武坛。\"]', '34', '0', '207', '0', '0', '1');
INSERT INTO `maps` VALUES ('210', '天微堂', '无', '[\"这里就是明教的天微堂，堂主就是威名远波的殷野王。他是白眉鹰王的独子和教主张无忌的舅舅。堂内陈设鲜丽奢华，显然甚是兴旺，往来教众进进出出，看他们各执兵刃，似乎是守总舵的卫士。\"]', '34', '213', '211', '212', '207', '1');
INSERT INTO `maps` VALUES ('211', '紫微堂', '无', '[\"这里就是明教的紫微堂，堂上的大匾上 “紫微贯日”四字并不因堂主殷素素早逝而有丝毫减色。堂中陈设一如往昔。\"]', '34', '0', '0', '210', '0', '1');
INSERT INTO `maps` VALUES ('212', '天市堂', '无', '[\"你一进入明教的天市堂，顿时感到一鼓肃杀之气。教众出入都十分小心，惟恐惊动了坐在油木桌后的堂主李天垣。见你进来李天垣抬眼看了看你，一张很柔和的脸却立马让你心惊肉跳。\"]', '34', '0', '210', '0', '0', '1');
INSERT INTO `maps` VALUES ('213', '明教大殿', '无', '[\"走进这里，突感一阵肃穆之意，昆仑山光明顶的明教大殿中十分空旷，殿顶高悬八盏金灯，两旁是精壮佩剑武士剑气森严逼人。正中设一宝座，前有一雕花檀木案，案头供明教玄铁令六枚。宝座后立有一扇玉屏，屏中所画正是明教圣火图，烈焰直逼日月。\"]', '34', '0', '0', '0', '210', '1');
INSERT INTO `maps` VALUES ('214', '驿道', '无', '[\"你走在一条青石大道上，人来人往非常繁忙，不时地有人骑着马匆匆而过。\"]', '2', '0', '124', '215', '0', '1');
INSERT INTO `maps` VALUES ('215', '驿道', '无', '[\"你走在一条青石大道上，人渐渐少起来，只是有时有人骑着马匆匆而过。\"]', '2', '0', '214', '219', '216', '1');
INSERT INTO `maps` VALUES ('216', '乱石岗', '无', '[\"这是一处乱石怪异的小山岗，向西便是通向成都的大路了.据说前方经常有野兽出没，不可久留。\"]', '2', '215', '0', '0', '0', '1');
