INSERT INTO `maps` VALUES ('218', '武汉', '无', '[\"繁华的九省通衢的江城，陆游曾写下“市邑雄富，列肆繁错，城外南市亦数里，虽钱塘、建康不能过，隐然一大都会也”可见其繁华。\"]', '24', '221', '219', '319', '220', '1');
