INSERT INTO `maps` VALUES ('242', '休息室', '无', '[\"这里是一个厢房，是供给远来的香客所居住的。厢房中央摆着几张大床，几张桌子。室中光线柔和，使人更增睡意。\"]', '16', '0', '240', '0', '0', '0');
INSERT INTO `maps` VALUES ('243', '后殿', '无', '[\"后殿规模不大。两边都是水磨石壁，镂刻着十八罗汉的肖像及佛谒，正中有一口水井，水面净如明镜，纯若菩提，名曰佛心乃寺内有名的清水井。\"]', '16', '246', '244', '245', '240', '1');
INSERT INTO `maps` VALUES ('244', '般若堂', '无', '[\"这是一间极为简陋的禅房。环视四周，除了几幅佛竭外，室内空荡荡地别无他物。地上整齐的摆放着几个破烂且发黑的黄布蒲团，几位须发花白的老僧正在坐在上面闭目入定。这里是本寺十八罗汉参禅修行的地方，不少绝世武功便是在此悟出。\"]', '16', '0', '0', '243', '0', '1');
INSERT INTO `maps` VALUES ('245', '罗汉堂', '无', '[\"这是一间极为简陋的禅房。环视四周，除了几幅佛竭外，室内空荡荡地别无他物。地上整齐的摆放着几个破烂且发黑的黄布蒲团，几位须发花白的老僧正在坐在上面闭目入定。这里是本寺十八罗汉参禅修行的地方，不少绝世武功便是在此悟出。\"]', '16', '0', '243', '0', '0', '1');
INSERT INTO `maps` VALUES ('246', '方丈室', '无', '[\"方丈室陈设简洁，正中一几，一蒲团，一矮床。北面墙上写着大大的一个『禅』字。一位慈眉善目的灰衣老僧正端坐于蒲团上打坐。\"]', '16', '249', '247', '248', '243', '1');
