INSERT INTO `maps` VALUES ('248', '武器库', '无', '[\"少林寺的武器库可称天下第一，十八般兵器，这里可谓应有尽有。四面一望，令你顿感眼花缭乱。墙上砌了一个个小格子，每个都插满了各种各样的兵器。墙角一个健壮的僧人正乒乒乓乓地在打造一根禅杖。另一位灰衣僧人正在墙角拂拭一把戒刀。看到你进来，他缓缓起身，向你迎了过来。\"]', '16', '0', '246', '0', '0', '1');
INSERT INTO `maps` VALUES ('249', '达摩院', '无', '[\"这是一座古朴的殿堂。西首的墙上挂满了各类武功图谱，不少白须白眉的老僧们正端坐在图谱画轴之前，似乎在苦苦思索。\"]', '16', '252', '250', '251', '246', '1');
INSERT INTO `maps` VALUES ('250', '后山', '无', '[\"三棵百年青松呈品字型巍然耸立，树干上各挖了个可容身一人的大洞，有个洞口露出黑色僧袍一角，似乎住得有人。中央是一片大草坪，中心处光秃秃的，似乎经常受大力击打，以致寸草不生。。\"]', '16', '0', '0', '249', '0', '1');
INSERT INTO `maps` VALUES ('251', '林中精舍', '无', '[\"你走进小舍，只见桌椅几榻，无一而非竹制，墙上悬着一幅墨竹，笔势纵横，墨迹淋漓，颇有森森之意。\"]', '16', '0', '249', '0', '0', '1');
INSERT INTO `maps` VALUES ('252', '竹林', '无', '[\"这是一片密密的竹林。这里人迹罕至，惟闻足底叩击路面，有僧敲木鱼声；微风吹拂竹叶，又如簌簌禅唱。令人尘心为之一涤，真是绝佳的禅修所在。\"]', '16', '831', '0', '0', '249', '1');
