INSERT INTO `maps` VALUES ('670', '华山', '无', '[\"这便是五岳中西岳华山，巍然耸立于陕西省华阴县城南。华山北瞰黄河，南连秦岭，五座山峰高低错落，各据一方，其间又有一段若断若续的山岭相连，疏密有度，神韵天成，远远望去，状若巨朵青莲，外围诸峰如莲瓣，中间三峰似莲蕊，因此山便因『花』得名，取名华山。\"]', '4', '669', '0', '0', '671', '1');
