INSERT INTO `maps` VALUES ('672', '村口', '无', '[\"这里是华山脚下，虽然远离县城，却没有什么山贼草寇在附近出没。东边是一个小村庄，村子里人家并不多，村民们过着日出而作，日落而息的宁静生活。\"]', '4', '0', '671', '673', '0', '1');
INSERT INTO `maps` VALUES ('673', '碎石路', '无', '[\"这是一个宁静的小村子，稀稀落落的分布着几十间土坯泥房，看来村中人家不多，而且大都生活很艰辛。\"]', '4', '674', '672', '676', '675', '1');
INSERT INTO `maps` VALUES ('674', '民宅', '无', '[\"这是一间很结实的房子，虽也是土坯房，但看的出建造的比村里其他的土坯房要好很多。墙上挂着、地下摆着一些农具。\"]', '4', '0', '0', '0', '673', '1');
INSERT INTO `maps` VALUES ('675', '铁匠铺', '无', '[\"这是一间甚是简陋的铁匠铺，入门正中是个大铁砧，满地煤渣碎铁，墙上挂着几张犁头，屋子的角落堆着几把破锄头、镰刀，靠墙边放着几把新出炉的铁锤。\"]', '4', '673', '0', '0', '0', '1');
INSERT INTO `maps` VALUES ('676', '打谷场', '无', '[\"这里是村子的中心，一个平坦的广场，每年秋收打谷就在这。广场东角有颗大槐树，平日茶余饭后，村里的男女老少都爱聚在这里谈天说地。\"]', '4', '0', '673', '0', '677', '1');
INSERT INTO `maps` VALUES ('677', '草棚', '无', '[\"这是一个简单的草棚，地上摆著一堆堆的货物，上山的游客可以在这买些上山用品。\"]', '4', '676', '0', '0', '0', '1');
INSERT INTO `maps` VALUES ('678', '云门', '无', '[\"过莎萝坪，是华山十八盘。十八盘的尽处就是“云门”了。这里刻着“仙境”、“天下第一名山”等石刻。云门之内的小盆地就是青柯坪。\"]', '4', '671', '0', '0', '679', '1');
INSERT INTO `maps` VALUES ('679', '青柯坪', '无', '[\"青柯坪是一山间盆地，地势略为平坦。从这里开始，上山的路都是由原地凿出的石阶。北壁下大石当路，此石叫做回心石，再上去山道奇险，游客至此，就该回头了。远远望见一个小小石亭，便是赌棋亭了。相传宋太祖与华夷先生曾弈棋于此，将华山作为赌注，宋太祖输了，从此华山上的土地就不须缴纳钱粮。\"]', '4', '678', '0', '680', '0', '1');
INSERT INTO `maps` VALUES ('680', '老君沟', '无', '[\"爬过两个要命的悬崖，可在这里稍作歇息。老君沟是一条半里多长的山沟，相传为太上老君所开，故名老君沟。\"]', '4', '0', '679', '0', '681', '1');
INSERT INTO `maps` VALUES ('681', '镇岳宫', '无', '[\"镇岳宫倚壁而构，四边松林蔽日，景色幽美。宫前有玉井，相传井中曾生千叶白莲。这里地势略为平坦，上、下山的游客多在此稍作休息再继续前行。附近有几间简陋的旅社，欲登东峰观日出者，大都于头晚歇息于此。\"]', '4', '680', '682', '684', '683', '1');
INSERT INTO `maps` VALUES ('682', '桃花林', '无', '[\"清幽的小谷，遍地桃花。是隔世高人隐居的地方。\"]', '4', '0', '0', '681', '0', '1');
INSERT INTO `maps` VALUES ('683', '桃花林', '无', '[\"清幽的小谷，遍地桃花。是隔世高人隐居的地方。\"]', '4', '681', '0', '0', '0', '1');
INSERT INTO `maps` VALUES ('684', '玉女峰', '无', '[\"这里是华山的中峰，玉女峰。传说春秋时隐士萧史善吹洞箫，箫声引动了秦穆公女儿弄玉的爱慕，抛弃了宫廷生活，跟萧史来此隐居，故称玉女峰。\"]', '4', '686', '681', '691', '685', '1');
INSERT INTO `maps` VALUES ('685', '思过崖', '无', '[\"这是华山的思过崖。危崖上有个山洞，是华山派历代弟子犯规后囚禁受罚之所。崖上光秃秃的寸草不生，更无一株树木。这危崖自来自来相传是玉女发钗上的一颗珍珠，当年华山派的祖师以此危崖为惩罚弟子之所，主要是此处无草无木，无虫无鸟，受罚的弟子在面壁思过之时，不致为外物所扰，心有旁鹜。 状态/选项\"]', '4', '684', '0', '0', '0', '1');
INSERT INTO `maps` VALUES ('686', '练武场', '无', '[\"这里是华山派的练武场，通常有许多华山派弟子在此习武练剑，所以不欢迎外人。北边的房屋门上挂着一块匾，上书「群仙观」，西北方似乎有条小道通往山上。\"]', '4', '688', '738', '687', '684', '1');
INSERT INTO `maps` VALUES ('687', '兵器房', '无', '[\"这里是华山派的兵器房，四周放这几个兵器架，上面是华山派各弟子平时练剑用的竹剑。\"]', '4', '0', '686', '0', '0', '1');
INSERT INTO `maps` VALUES ('688', '群仙观', '无', '[\"这里是华山派历来之根本重地，墙上挂着华山派历代掌门人的画像，华山派一切重大礼仪都在这里举行。这里也是华山弟子讲武习剑之地。北边就是华山掌门岳不群的居所「有所不为轩」。\"]', '4', '690', '0', '689', '686', '1');
INSERT INTO `maps` VALUES ('689', '华山休息室', '无', '[\"这里是华山弟子饮食起居的地方。靠墙一溜摆着几张木床，床上铺的是棉被。使人一看就想睡觉。\"]', '4', '0', '688', '0', '0', '0');
INSERT INTO `maps` VALUES ('690', '有所不为轩', '无', '[\"这就是华山掌门岳不群的居所「有所不为轩」，此处乃是前厅，厅上挂着「正气堂」三字匾额。厅中很宽敞，却没什么摆设，靠墙放着两把交椅。这里乃是岳不群议事及授徒的地方\"]', '4', '0', '0', '0', '688', '1');
INSERT INTO `maps` VALUES ('691', '舍身崖', '无', '[\"这是一段极窄极险的山崖，四周云雾飞绕，下面渊深无底。如不小心，一失足掉下去，只怕连骨头都找不到。\"]', '4', '693', '684', '694', '692', '1');
INSERT INTO `maps` VALUES ('692', '玉女祠', '无', '[\"玉女祠里供奉的到底是哪位玉女，有许多不同的说法，现在所能看到的只一尊呆板的石像。祠中大石上有一处深陷，凹处积水清碧，这是玉女的洗脸盆，碧水终年不干\"]', '4', '691', '0', '0', '0', '1');
INSERT INTO `maps` VALUES ('693', '苍龙岭', '无', '[\"华山五峰中最惊险的是北峰，北峰之上最陡峭的则是苍龙岭。此岭高约百米，宽仅一米，而登山之路则在其山脊之上，两侧脊坡光滑挺拔，其下悬崖深邃，云涛隐伏，岭体蜿蜒逶迤，体青背黑，如苍龙腾空，故有是名。\"]', '4', '0', '0', '0', '691', '1');
INSERT INTO `maps` VALUES ('694', '上天梯', '无', '[\"此处绝险，必须面壁挽索，贴身探足前进，步不登高，故名上天梯。\"]', '4', '695', '691', '729', '887', '1');
INSERT INTO `maps` VALUES ('695', '紫气台', '无', '[\"紫气台终年云遮雾掩，迷蒙不见天日，更为怪异的是：每逢下雨天，山谷之中就隐隐有紫气出现。极目四望，风景如画。在这里隐居相必非常惬意。\"]', '4', '0', '0', '0', '694', '1');
INSERT INTO `maps` VALUES ('696', '朝阳峰', '无', '[\"这里就是华山的东峰，也称朝阳峰，是华山看日出的绝佳处。於此俯瞰，黄河隐显东迤如带，南望小孤峰背倚山河。小孤峰上有一小亭，覆以铁瓦，亭中摆有铁棋盘一局，那就是华山著名的“博台”了。远望东壁悬崖上，更有一个巨型掌印，相传为「河神巨灵」劈山时所留下的，李白诗中“巨灵咆哮劈两山，洪波喷流射东海”就是指这个上古传说。\"]', '4', '0', '694', '0', '0', '1');
