INSERT INTO `maps` VALUES ('744', '正厅', '古墓正厅', '[\"这里是古墓的正厅，本来应该停放灵柩棺木之类的东西，却不知道被人搬到哪里去了。\"]', '37', '746', '743', '747', '756', '1');
INSERT INTO `maps` VALUES ('745', '休息室', '古墓休息室', '[\"这是间很大的房间，光线非常昏暗。房里没有什么别的东西，只有中间放着一张收拾得舒舒服服的大床，床上的被褥也整理得整整洁洁，看着就让人想睡觉。\"]', '37', '0', '0', '755', '0', '0');
INSERT INTO `maps` VALUES ('746', '练功房', '古墓北练功房', '[\"这座石室形状甚是奇特，前窄後宽，成为梯形，东边半圆西边却作三角形状，原来这是以前重阳祖师的练功室，前窄练掌，後宽使拳，东圆研剑，西角发镖。南面有个小门洞，通向里面的练功室。\"]', '37', '0', '0', '0', '744', '1');
