INSERT INTO `maps` VALUES ('768', '大观', '无', '[\"逍遥派的道观，比较简陋，只有一些弟子在这里打扫。里面的庭院中央摆放着一个圆炉。起烟很稀，说明没多少人来。\"]', '39', '769', '766', '0', '770', '1');
