INSERT INTO `maps` VALUES ('770', '铁索桥', '无', '[\"大观出来正前方有一条小河，上面搭着一条铁索桥。\"]', '39', '768', '0', '0', '771', '1');
INSERT INTO `maps` VALUES ('771', '天机坛', '无', '[\"天机坛是逍遥派的练功广场，这里聚集了很多逍遥派弟子。广场很大，在后方有一个巨大的祭天香炉。\"]', '39', '770', '764', '774', '773', '1');
