INSERT INTO `maps` VALUES ('778', '书院', '无', '[\"逍遥派的书院是最大的，也是藏书最多的。书院两层楼，前半部分只有一层，后半部分是双层阁楼，整齐的摆放着书籍。\"]', '39', '0', '775', '0', '0', '1');
