INSERT INTO `maps` VALUES ('799', '涌金门', '无', '[\"杭州十大古城门之涌金门。涌金门为古代杭州西城门之一。五代天福元年，吴越王钱元瓘引西湖水入城，在此开凿涌金池，筑此门，门濒湖，东侧有水门。传说为西湖中金牛涌现之地，因而得名。\"]', '23', '800', '804', '366', '822', '1');
INSERT INTO `maps` VALUES ('800', '湖边小亭', '无', '[\"一个小亭，提供给游客歇脚的。这里有许许多多的游客。\"]', '23', '801', '0', '0', '799', '1');
INSERT INTO `maps` VALUES ('801', '断桥残雪', '无', '[\"断桥残雪是西湖上著名的景色，以冬雪时远观桥面若隐若现于湖面而称著。属于西湖十景之一。\"]', '23', '0', '802', '0', '800', '1');
INSERT INTO `maps` VALUES ('802', '锦带桥', '无', '[\"右挹外湖，又可近眺平湖秋月的露台，而孤山则近在咫尺，绿树掩映楼台，夏时荷香醉人。\"]', '23', '0', '803', '801', '0', '1');
INSERT INTO `maps` VALUES ('803', '平湖秋月', '无', '[\"杭州西湖，历来是最佳赏月之地。在白堤西端，就有一处月白风清的地方，那就是西湖十景之一的平湖秋月景区，它背靠孤山，面临西湖的外湖，景观沿湖一排敞开，包括御碑亭、水面平台、四面厅、八角亭、湖天一碧楼等建筑。\"]', '23', '0', '808', '802', '805', '1');
INSERT INTO `maps` VALUES ('804', '湖面石桥', '1', '[\"距离湖面很低的石桥，与西湖的景色十分协调。\"]', '23', '0', '823', '799', '0', '1');
INSERT INTO `maps` VALUES ('805', '湖面石桥', '2', '[\"距离湖面很低的石桥，与西湖的景色十分协调。\"]', '23', '803', '0', '0', '823', '1');
INSERT INTO `maps` VALUES ('806', '湖面石桥', '3', '[\"距离湖面很低的石桥，与西湖的景色十分协调。\"]', '23', '823', '0', '0', '818', '1');
INSERT INTO `maps` VALUES ('807', '湖面石桥', '4', '[\"距离湖面很低的石桥，与西湖的景色十分协调。\"]', '23', '0', '811', '823', '0', '1');
INSERT INTO `maps` VALUES ('808', '楼外楼', '无', '[\"这里的景色十分优美，楼层是层层叠加上去的，在西湖是一大观景台。\"]', '23', '0', '809', '803', '0', '1');
INSERT INTO `maps` VALUES ('809', '压堤桥', '无', '[\"压堤桥是苏堤的第三桥,是一座半圆石拱桥。是眺望全湖的最佳处之一,故名“压堤”,旧时通茅家埠港。\"]', '23', '0', '0', '808', '810', '1');
INSERT INTO `maps` VALUES ('810', '西泠桥', '无', '[\"西泠桥是一座古色古香的环洞石拱桥。西泠桥与长桥、断桥并称为西湖三大情人桥。位于西霞岭麓到孤山之间，又名西林桥。\"]', '23', '809', '0', '0', '811', '1');
INSERT INTO `maps` VALUES ('811', '望山桥', '无', '[\"望山桥又名便山桥，是苏州山塘街西起点，位于虎丘正门前，相传唐白居易站在此桥观望虎丘山而得名。\"]', '23', '810', '0', '807', '812', '1');
INSERT INTO `maps` VALUES ('812', '石桥', '杭州西湖唯一的石桥', '[\"一座普通的石桥，在西湖的美景衬托下，它非常的别致。\"]', '23', '811', '0', '0', '813', '1');
INSERT INTO `maps` VALUES ('813', '锁澜桥', '无', '[\"苏堤的第二桥，在桥上可观不远湖对面处的汪庄，桥长16.9米，净宽6.4米，单孔净跨6.2米，是一座半圆石拱桥，始建于北宋，民国九年至十一年改石级为斜坡，一九五四年拱桥改为青石桥栏，旧时通赤山麦岭路。\"]', '23', '812', '0', '0', '814', '1');
INSERT INTO `maps` VALUES ('814', '映波桥', '无', '[\"苏堤上的六桥由南而北，‘映波’是第一桥，站在桥头可见湖面上映有花港观鱼内的亭台楼阁、长廊水榭的倒影。民国九年桥面改石级为斜坡。现桥栏上装饰有跃狮、蝴蝶、回纹图案。\"]', '23', '813', '0', '816', '815', '1');
INSERT INTO `maps` VALUES ('815', '凉亭', '西湖凉亭', '[\"供游客乘凉歇息的凉亭。凉亭旁边种着两排柳树，微风吹过，柳条“沙沙”响。\"]', '23', '814', '0', '0', '0', '1');
INSERT INTO `maps` VALUES ('816', '净慈寺', '无', '[\"净慈寺，位于浙江省杭州市的西湖南岸，雷峰塔对面，是西湖历史上四大古刹之一，中国著名寺院之一。因为寺内钟声洪亮，所以‘南屏晚钟’成为“西湖十景”之一。\"]', '23', '0', '814', '817', '0', '1');
INSERT INTO `maps` VALUES ('817', '客栈', '杭州西湖客栈', '[\"这里人来人往，游客非常多，自然客栈的客人也非常的多。客栈五层高，在客栈可以一览西湖美景。\"]', '23', '818', '816', '819', '0', '0');
INSERT INTO `maps` VALUES ('818', '夕照山', '无', '[\"夕照山的雷峰塔与宝石山的保俶塔隔湖相映,由此形成了 一山、二塔、三岛、三堤、五湖 的基本格局。\"]', '23', '806', '0', '0', '817', '1');
INSERT INTO `maps` VALUES ('819', '柳浪闻莺', '无', '[\"柳浪闻莺是西湖十景之五，位于西湖东南岸，清波门处的大型公园。分友谊、闻莺、聚景、南园四个景区。\"]', '23', '0', '817', '820', '0', '1');
INSERT INTO `maps` VALUES ('820', '湖边小亭', '2', '[\"西湖的湖边小亭，供游客歇脚的地方。走累了可以坐坐。\"]', '23', '821', '819', '0', '0', '1');
INSERT INTO `maps` VALUES ('821', '清波门', '无', '[\"杭州十大古城门之清波门。清波门在五代吴越时为水门，南宋绍兴二十八年增筑杭城，为门十三，清波门是西城门之一，门濒湖之东南，取“清波”之意名门，历代沿用。因流福暗沟引湖水入城，俗称暗门。\"]', '23', '822', '0', '0', '820', '1');
